mapper.update:
UPDATE review1 SET review_score1 = #{review_score1},
 	review_content1 = #{review_content1},
    review_file1 = #{review_file1, jdbcType=VARCHAR}
WHERE review_id1 = #{review_id1}

mapper.select:
SELECT * FROM (
    SELECT a.*, ROWNUM rnum FROM (
        SELECT * FROM review1
        WHERE res_id = #{res_id}
        ORDER BY review_register1 DESC
    ) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}
